import * as React from 'react';
import { connect } from 'react-redux';
import { getUsers } from "../actions/users";

class Read extends React.Component {
   componentDidMount() {
       this.props.getUsers();
   };

   render() {
       if (this.props.loading) {
           return <div>Loading</div>
       }

       if (this.props.error) {
           return <div style={{ color: 'red' }}>ERROR: {this.props.error}</div>
       }

       return (
           <table>
               <thead>
                   <tr>
                       <th>First Name</th>
                       <th>Last Name</th>
                       <th>Active?</th>
                       <th>Posts</th>
                       <th>Messages</th>
                   </tr>
               </thead>
               <tbody>
               {this.props.items.map(u =>
                   <tr key={u.id}>
                       <td>{u.firstName}</td>
                       <td>{u.lastName}</td>
                       <td>{u.active ? 'Yes' : 'No'}</td>
                       <td>{u.posts}</td>
                       <td>{u.messages}</td>
                   </tr>
               )}
               </tbody>
           </table>
       );
   }
}

const mapStateToProps = state => ({
   items: state.reduxThunk.items,
   getUsers: state.reduxThunk.getUsers,
   error: state.reduxThunk.error,
});

const mapDispatchToProps = {
  getUsers
};

export default connect(
   mapStateToProps,
   mapDispatchToProps
)(Read);

