import history from '../history';
import api from "../services/api";
export const RECEIVE_ARTICLES = 'GET_ARTICLES';
export const ADD_ARTICLE = 'ADD_ARTICLE';
export const RECEIVE_ARTICLE = 'RECEIVE_ARTICLE';
export const REMOVE_ARTICLE = 'REMOVE_ARTICLE';
export const UPDATE_ARTICLE = 'UPDATE_ARTICLE';
export const REPLACE_ARTICLE = 'REPLACE_ARTICLE';



export const getArticles = () => {
  return (dispatch) => {
    console.log("sachin")
    return api.get(`/v1/users`)
      .then(response => {
        dispatch({type: RECEIVE_ARTICLES, articles: response.data})
      })
      .catch(error => { throw(error); });
  };
};

export const addArticle = ({ name, email, password, role }) => {
  return (dispatch) => {
    return api.post(`/v1/users`, {name, email, password, role})
      .then(response => {
        const data = response.data;
        dispatch({type: ADD_ARTICLE, payload: {id: data.id, name: data.name, email: data.email, password: data.password, role: data.role}})
      })
      .then(() => {
        history.push("/articles")
      })
      .catch(error => { throw(error) });
  };
};

export const getArticle = () => {
  return (dispatch) => {
    return api.get(`/v1/users/:userId`)
      .then(response => {
        dispatch({type: RECEIVE_ARTICLE, article: response.data});
      })
      .catch(error => { 
        throw(error); 
      });
  };
};

export const deleteArticle = (id) => {
  return (dispatch) => {
    return api.delete(`/v1/users/${id}`)
      .then(response => {
        dispatch({type: REMOVE_ARTICLE, payload: {id}})
      })
      .then(() => {
        history.push("/articles")
      })
      .catch(error => {
        throw(error);
      });
  };
};

export const updateArticle = (article) => {
  const articleId = article.id;
  return (dispatch) => {
    return api.patch(`/v1/users/${article.id}`, {name: article.name, email: article.email, password: article.password, role: article.role})
      .then(response => {
        const data = response.data;
        dispatch({type: UPDATE_ARTICLE, payload: {id: data.id, name: data.name, email: data.email, password: data.password, role: data.role}})
        dispatch({type: REPLACE_ARTICLE, payload: {id: data.id, name: data.name, email: data.email, password: data.password, role: data.role}})
      })
      .then(() => {
        history.push(`/articles/${articleId}`)
      })
      .catch(error => { throw(error) });
  };
};
