import React, { Component } from 'react';
import { connect } from 'react-redux';
import { loadUsers } from '../actions/authActions';

import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';
class Dashboard extends Component {
  // componentDidMount() {
  //   this.props.loadUsers();
  // }

  componentDidMount() {
		if (this.props.users[0].id === '') {
			this.props.actions.loadUsers();
		}
	};

  render() {
    const users = this.props.users;
    return (
      <div>
        <ul className="list-group">
			{props.users.map(users => <li className="list-group-item" key={user.id}> {user.name} {user.email} {user.role}</li>)}
		</ul>
      </div>
    )
  }
}

// Define here 1) the action functions used below in connect; 2) the prop in the root reducer reducers/index.js corresponding to the current component
Dashboard.propTypes = {
  loadUsers: PropTypes.func.isRequired,
  users: PropTypes.object.isRequired,
  actions: PropTypes.object.isRequired,

}

// Send slice of Redux state to reducer
// convert the global state obtained from redux to local props
// function mapStateToProps(state) {
//   return {
//     users: state.users
//     // data: state.data //data because it is in root reducer reducers/index.js
//   };
// }

const mapStateToProps = (state, ownProps) => {
	// state = { users: [{ id: 1, first_name: "Sean", last_name: "Delaney" }, etc.] }

	if (state.users.length > 0) {
		return {
			users: state.users
		};
	} else {
		return {
			users: [
				{
					id: '', 
					name: '', 
					email: '',
                    role: '' 
				}
			]
		};
	}
};



const mapDispatchToProps = dispatch => {
	return {
		actions: bindActionCreators(loadUsers, dispatch)
	};
};

export default connect(mapStateToProps,mapDispatchToProps)(Dashboard);

