import {
  CREATE_USER_REQUEST,
  CREATE_USER_SUCCESS,
  CREATE_USER_FAILURE,
  GET_USERS_REQUEST,  
  GET_USERS_SUCCESS,  
  GET_USERS_FAILURE,  
  GET_USER_BYID_REQUEST,
  GET_USER_BYID_SUCCESS,
  GET_USER_BYID_FAILURE,
  DELETE_USER_BYID_REQUEST,
  DELETE_USER_BYID_SUCCESS,
  DELETE_USER_BYID_FAILURE,
} from "../actions/usersActiontypes";
import api from "../services/api";

export const GethUsers = () => {  
  return (dispatch) => {  
    dispatch(getUsersRequest())  
    api 
      .get('/v1/users')  
      .then(response => {  
        const users = response.data  
        localStorage.setItem("token", response.data.tokens.access.token);
        console.log(response.data);  
        dispatch(getUsersSuccess(users))  
      })  
      .catch(error => {  
        dispatch(getUsersFailure(error.message))  
      })  
  }  
} 

export const getUsersRequest = () => {  
  return {  
    type: GET_USERS_REQUEST  
  }  
}  

export const getUsersSuccess = users => {  
  return {  
    type: GET_USERS_SUCCESS,  
    payload: users  
  }  
}  

export const getUsersFailure = error => {  
  return {  
    type: GET_USERS_FAILURE,  
    payload: error  
  }  
}  

export function createUser(data, history) {
  return (dispatch) => {
    dispatch({ type: CREATE_USER_REQUEST });
    api
      .post('/v1/users', data)
      .then(function (res) {
        console.log("res=>", res.data);
        localStorage.setItem("token", res.data.tokens.access.token);
        setTimeout(() => {
          dispatch({
            type: CREATE_USER_SUCCESS,
            payload: res.data,
          });
          history.push("/");
        }, 1000);
      })
      .catch(function (error) {
        const { response } = error;
        console.log("err", response);
        if (response !== undefined) {
          dispatch({
            type: CREATE_USER_FAILURE,
            payload: response.data,
          });
          alert(response.data?.message);
        }
      });
  };
}



export function getUserById(id) {
  return (dispatch) => {
    dispatch({ type: GET_USER_BYID_REQUEST });
    api
      .get(`/v1/users/${id}`)
      .then(function (res) {
        console.log("res =>", res.data);
        dispatch({
          type: GET_USER_BYID_SUCCESS,
          payload: res.data,
        });
      })
      .catch(function (error) {
        const { response } = error;
        console.log("err", response);
        if (response !== undefined) {
          dispatch({
            type: GET_USER_BYID_FAILURE,
            payload: response.data,
          });
        }
      });
  };
}

