  return (dispatch) => {
    dispatch({ type: DELETE_USER_BYID_REQUEST });
    api
      .delete(`/v1/users/${id}`)
      .then(function (res) {
        console.log("res =>", res.data);
        dispatch({
          type: DELETE_USER_BYID_SUCCESS,
          payload: id,
        });
      })
      .catch(function (error) {
