import React, { useEffect, useState } from "react";
import { Card, Form, Button } from 'react-bootstrap';
import { getUserAction, updateUserAction } from '../actions/creators';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';


const Update = () => {
    const dispatch = useDispatch();
   
    const { id } = useParams();

    const [state, setState] = useState({
        name: '',
        email: '',
        role: '' ,
        
        
    }
    );

    const  result  = useSelector((state) => state.users?.results);
    console.log(result)
    useEffect(() => {
        dispatch(getUserAction(id));
    }, []);

    useEffect(() => {
        if (result) {
            setState({ ...result });
        }
    }, [result]);

    const { name, email, role } = state;

    const handleTextChange = e => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
        console.log(state);
    }

    const handleOnSubmit = (e) => {
        e.preventDefault();
        dispatch(updateUserAction(state, id));
    

    }

    return (
        <div className="container" id="update" >
            <Card className={"border border-dark bg-dark text-white"} >
                <Card.Header><h3>Edit User</h3></Card.Header>
                <div className="form-container">
                    <Form onSubmit={handleOnSubmit}>
                        <Card.Body >
                            <div className="form-group" >
                                <label>Name</label>
                                <input
                                    type="text" name="name"
                                    onChange={handleTextChange}
                                    value={name || ""}
                                    className="form-control w-50 p-2"
                                    required
                                />
                            </div>
                            <div className="form-group" >
                                <label>Email address</label>
                                <input
                                    type="email"
                                    name="email"
                                    onChange={handleTextChange}
                                    value={email || ""}
                                    className="form-control w-50 p-2"
                                    required
                                />
                            </div>
                             <div className="form-group" >
                                <label>Role</label>
                                <input
                                    type="text"
                                    name="role"
                                    onChange={handleTextChange}
                                    value={role || ""}
                                    className="form-control w-50 p-2"
                                    required
                                />
                            </div> 
                           

                            <br></br>


                            <div className="form-group">
                                <button onChange={handleTextChange}  className="btn btn-primary" type="submit">Edit User</button>
                                <Button id="btn" href='/dashboard' className="w-30 p-3 float-right" variant="primary" >
                                    Back
                                </Button>



                            </div>

                        </Card.Body>
                    </Form>
                </div>
            </Card >
            {/* )} */}
        </div >
    );
}


export default Update;
