/// fetch data of a single user  basedin id
export const getUserAction = (id) => {
    return (dispatch) => {
        api.get(`/v1/users/${id}`)
            .then(response => {
                console.log(response);
                dispatch(getUser(response.data)
                );
            })
            .catch(error => {
                console.log(error);
            });
    }
}

/// fetch all users 
export const getUsersAction = () => {
    return (dispatch) => {
        api.get('v1/users')
            .then(response => {
                console.log(response);
                /// dispatch function dispatches an action which triggers state changes in the store
                dispatch(getUsers(response.data)
                );

            })
            .catch(error => {
                console.log(error);
            });
    }
}

/// delete a user
export const deleteUserAction = (id) => {
    return (dispatch) => {
        api.delete(`/v1/users/${id}`)
            .then(response => {
                console.log(response);
                dispatch(deleteUser());
                dispatch(getUsersAction());
            })
            .catch(error => {
                console.log(error);
            });
    }
}

/// update the existing data of a user
export const updateUserAction = (id,data) => {
    return (dispatch) => {
        api.patch(`/v1/users/${id}`,data)
            .then(response => {
                console.log(response);
                dispatch(updateUser());
                dispatch(getUsersAction());
            })
            .catch(error => {
                console.log(error);
            });
    }
}
