  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {
    this.props.getUsers();
  }

  handleDelete = (id) => {
    const result = window.confirm("Are you sure to delete?");
    if (result) {
      this.props.deleteUserById(id);
    }
  };

  render() {
