import React from 'react';
import { connect } from 'react-redux';
import { addArticle } from '../actions/user';

class ArticleAdd extends React.Component {
  state = { name: '', email: '', password: '', role: '' };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.addArticle(this.state);
  };

  render() {
    return (
      <div>
        <h4>Add Article</h4>
        <form onSubmit={ this.handleSubmit }>
          <div className="form-group">
            <input type="text" name="name" required value={this.state.name} onChange={this.handleChange} className="form-control" placeholder="Name" />
          </div>
          <div className="form-group">
            <input type="text" name="email" required value={this.state.email} onChange={this.handleChange} className="form-control" placeholder="Content" />
          </div>
          <div className="form-group">
            <input type="password" name="password" required value={this.state.password} onChange={this.handleChange} className="form-control" placeholder="Content" />
          </div>
          <div className="form-group">
            <input type="text" name="role" required value={this.state.role} onChange={this.handleChange} className="form-control" placeholder="Content" />
          </div>
          <button type="submit" className="btn btn-dark">Create</button>
        </form>
      </div>
    );
  }
}

const mapDispatchToProps = { addArticle };

export default connect(null, mapDispatchToProps)(ArticleAdd);
