import React from "react";
import { connect, useDispatch } from "react-redux";
import { useHistory, Redirect } from "react-router-dom";
import { signUp } from "../../actions/authActions";
import PropTypes from "prop-types";

import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';


const Register = ({  signUp, isLoggedIn }) => {

	const validationSchema = Yup.object().shape({
		name: Yup.string().required('Name is required'),
		email: Yup.string()
		  .required('Email is required')
		  .email('Email is invalid'),
		password: Yup.string()
		  .required('Password is required')
		  .min(6, 'Password must be at least 6 characters')
		  .max(8, 'Password must not exceed 40 characters'),
		acceptTerms: Yup.bool().oneOf([true], 'Accept Terms is required')
	  });

	//   const {
	// 	formData,
	// 	handleSubmit,
	// 	reset,
	// 	formState: { errors }
	//   } = useForm({
	// 	resolver: yupResolver(validationSchema)
	//   });

	const {formData, setFormData,reset,formState: {errors}} = useForm({
			
		resolver: yupResolver(validationSchema)
	});
	// const [validation, setValidation] = useState({
	// 	name: "",
	// 	email: "",
	// 	password: "",
	//   });


	//  const { name, email, password } = formData;
	const history = useHistory();
	const dispatch = useDispatch();
	const onChange = (e) =>
		setFormData({  [e.target.name]: e.target.value });
	
	const onSubmit = async (e) => {
		console.log("Form data", e);
		e.preventDefault();
	
  
		dispatch(signUp(formData,history));
			 signUp({ name, email, password });
			
			};

	// Redirect if logged in
	if (isLoggedIn) {
		return <Redirect to="/login" />;
	}

	return (
		<div className="register-form">
		<form onSubmit={(e) => onSubmit(e)}>
		  <div className="form-group">
			<label>Name</label>
			<input
			  name="name"
			  type="text"
			  {...signUp(name)}
			  
			  className={`form-control ${errors.name ? 'is-invalid' : ''}`}
			  onChange={(e) => onChange(e)}
			/>
			<div className="invalid-feedback">{errors.name?.message}</div>
		  </div>
  
		  <div className="form-group">
			<label>Email</label>
			<input
			  name="email"
			  type="text"
			 
			 {...signUp(email)}
			  className={`form-control ${errors.email ? 'is-invalid' : ''}`}
			  onChange={(e) => onChange(e)}
			/>
			<div className="invalid-feedback">{errors.email?.message}</div>
		  </div>
  
		  <div className="form-group">
			<label>Password</label>
			<input
			  name="password"
			  type="password"
			  {...signUp('password')}
			  className={`form-control ${errors.password ? 'is-invalid' : ''}`}
			  onChange={(e) => onChange(e)}
			/>
			<div className="invalid-feedback">{errors.password?.message}</div>
		  </div>
  
		  <div className="form-group form-check">
			<input
			  name="acceptTerms"
			  type="checkbox"
			  {...formData('acceptTerms')}
			  className={`form-check-input ${
				errors.acceptTerms ? 'is-invalid' : ''
			  }`}
			/>
			<label htmlFor="acceptTerms" className="form-check-label">
			  I have read and agree to the Terms
			</label>
			<div className="invalid-feedback">{errors.acceptTerms?.message}</div>
		  </div>
  
		  <div className="form-group">
			<button type="submit" className="btn btn-primary">
			  Register
			</button>
			<button
			  type="button"
			  onClick={reset}
			  className="btn btn-warning float-right"
			>
			  Reset
			</button>
		  </div>
		</form>
	  </div>
		// <div className="register-form">
		// 	<h1 className="heading">Sign Up</h1>
		// 	<p className="lead">
		// 		<i className="fas fa-user"></i> Create Your Account
		// 	</p>
		// 	{/* <Alert /> */}
		// 	<br />
		// 	<form className="form" onSubmit={(e) => onSubmit(e)}>
		// 		<div className="form-group">
		// 			<input
						
		// 				type="text"
		// 				placeholder="Name"
		// 				name="name"
		// 				value={formData.name}
		// 				onChange={(e) => onChange(e)}
						
						
		// 			/>
		// 			 <div  className={`invalid-feedback ${validation.name ? 'is-invalid' : ''}`}>{validation.name}</div>
		// 			  {/* {validation.name && <p>{validation.name}</p>} */}
		// 		</div>
		// 		<div className="form-group">
		// 			<input
		// 				type="email"
		// 				placeholder="Email Address"
		// 				name="email"
		// 				value={formData.email}
		// 				onChange={(e) => onChange(e)}
		// 			/>
		// 			 {validation.email && <p>{validation.email}</p>}
		// 		</div>
		// 		<div className="form-group">
		// 			<input
		// 				type="password"
		// 				placeholder="Password"
		// 				name="password"
		// 				minLength="6"
		// 				value={password}
		// 				onChange={(e) => onChange(e)}
		// 			/>
					
		// 		</div>
				
		// 		<button type="submit" className="btn btn-primary" href="/login">Register</button>
				
		// 	</form>
		// 	<p className="link">
		// 		Already have an account? <Link to="/login">Sign In</Link>
		// 	</p>
		// </div>
	);
};

Register.propTypes = {
	signUp: PropTypes.func.isRequired,
	isLoggedIn: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isLoggedIn: state.auth.isLoggedIn,
});

export default connect(mapStateToProps, {  signUp })(Register);
