import React, { useEffect, useState } from "react";
import { Card, Form, Button } from 'react-bootstrap';
import { getUserAction } from '../actions/creators';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';

const ViewDetails = () => {

    let dispatch = useDispatch();


    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        role: ''
    }
    );
    let { id } = useParams();
    const { user } = useSelector((state) => state.user);

    useEffect(() => {
        dispatch(getUserAction(id));
    }, []);

    useEffect(() => {
        if (user) {
            setState({ ...user });
        }
    }, [user]);

    const { name, email, password, role } = state;

    return (
        <div className="container" id="update" >

            <Card className={"border border-dark bg-dark text-white"} >
                <Card.Header id="header" ><h3 className="profile">PROFILE DETAILS</h3></Card.Header>
                <Form>
                    <Card.Body id="body" >
                        <div className="container">
                            <div className="profile-item">
                                <h3><label id="label">Name :</label> {name}</h3>
                            </div>
                            <div className="profile-item">
                                <h3> <label id="label">Email :</label> {email}</h3>
                            </div>
                            <div className="profile-item">
                                <h3> <label id="label">Password :</label> {password}</h3>
                            </div>
                            <div className="profile-item">
                                <h2 className=" p-3 "> <label id="label">Role </label><br></br> {role}</h2>
                            </div>
                            <Button id="btn" href='/' className="w-30 p-3 float-right" variant="primary" >
                                Home
                            </Button>
                            <Button id="btn" href={'/update/' + id} className="w-30 p-3 float-right" variant="primary" >
                                Update
                            </Button>
                        </div>



                    </Card.Body>
                </Form>
            </Card>

        </div >
    );

}


export default ViewDetails;
