import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import {
  makeStyles,
  TextField,
  Button,
  CircularProgress,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { getUserById, updateUserById } from "../actions/user";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "50ch",
    },
    buttonProgress: {
      color: "#fff",
      position: "absolute",
      top: "50%",
      left: "50%",
      marginTop: -12,
      marginLeft: -12,
    },
  },
}));

function Update() {
  const classes = useStyles();
  const history = useHistory();
  const { id } = useParams();
  const dispatch = useDispatch();
  const loading = useSelector((state) => state.users?.loading);
  const  user  = useSelector((state) => state.users?.results);
  console.log(user)
  const [inputs, setInputs] = useState({
    name: "",
    email: "",
    role: ""
  });
  // const [submitted, setSubmitted] = useState(false);
// const {name,email,role} = inputs;
  useEffect(() => {
    dispatch(getUserById(id));
  }, []);

  useEffect(() => {
    if (user) {
      setInputs({...user});
    }
  }, [user]);
  const { name, email, role } = inputs;
  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });
    console.log(inputs);
    // let { name, value } = e.target;
    // setState({ ...state, [name]: value });
    
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    // setSubmitted(true);
    // if (!inputs.name || !inputs.email || !inputs.role) {
    //   return;
   
    // }
    dispatch(updateUserById(id, inputs));
    history.push("/dashboard");

  
    
  }

  return (
    <React.Fragment>
      <h1 style={{ textAlign: "center" }}>Update User</h1>
      <form
        className={classes.root}
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        <TextField
          type="text"
          name="name"
          label="Name"
          value={name || ""}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          type="text"
          name="email"
          label="Email"
          value={email || ""}
          onChange={handleChange}
          fullWidth
        />
          <TextField
          type="text"
          name="role"
          label="role"
          value={role || ""}
          onChange={handleChange}
          fullWidth
        />
        <Button
          disabled={loading}
          type="submit"
          variant="contained"
          color="primary"
          onChange={handleChange}
        >
          Submit
        </Button>
        {loading && (
          <CircularProgress size={24} className={classes.buttonProgress} />
        )}
      </form>
    </React.Fragment>
  );
}

export default Update;
